import React, { Component } from "react";
export default class Search extends React.Component {
    state = {
        search: '',
        type: 'all'
    }
    handleDown = (e) => {
        if (e.key === "Enter") {
            this.props.searchMovies(this.state.search)
        }
    }
    handleFilter = (e) => {
        this.setState({ type: e.target.dataset.type })
    }
    render() {
        return (
            <div class="row">
                <div class="col s12">
                    <div class="input-field">
                        <input
                            id="email_inline"
                            type="search"
                            placeholder="Search"
                            class="validate"
                            value={this.state.search}
                            onChange={(e) => this.setState({ search: e.target.value })}
                            onKeyDown={this.handleDown}
                        />
                        <button className="btn search-btn" onClick={() => this.props.searchMovies(this.state.search)}>Search Movies</button>
                    </div>
                </div>
                <div>
                    <label>
                        <input class="with-gap"
                            name="type"
                            type="radio"
                            data-type="all"
                            onChange={this.handleFilter} 
                            checked={this.state.type === 'all'} />
                        <span>All</span>
                    </label>
                    <label>
                        <input class="with-gap"
                            name="type" type="radio"
                            data-type="movie"
                            onChange={this.handleFilter}
                             />
                        <span>Movies only</span>
                    </label>
                    <label>
                        <input class="with-gap" name="type" type="radio" data-type="serias" onChange={this.handleFilter} />
                        <span>Serials only</span>
                    </label>
                </div>
            </div>
        )
    }
}
